    <script>
        $(document).ready(function () {
            const firstPageUrl = $('#loadAllProductMale').attr('href');
            if (window.location.href !== firstPageUrl) {
                $("#contentProductMale").load("?module=home/pageHome/cateMale&action=allItems");
            }
        });
        $("#loadAllProductMale").click(function (e) {
            e.preventDefault();
            $("#contentProductMale").load("?module=home/pageHome/cateMale&action=allItems");
        });
        //
        $("#linkProductMale").on("click", "a", function () {
            var hrf = $(this).attr("href");
            var link = hrf.substring(1, hrf.length);
            $("#contentProductMale").load(link);
        });
        // 
     

    </script>
